<?php

// src/Repository/VersionRepository.php

namespace App\AppBundle\Repository;

use App\AppBundle\Entity\Version;
use Doctrine\Bundle\DoctrineBundle\Repository\ServiceEntityRepository;
use Doctrine\Persistence\ManagerRegistry;

/**
 * VersionRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class VersionRepository extends ServiceEntityRepository
{
    public function __construct(ManagerRegistry $registry)
    {
        parent::__construct($registry, Version::class);
    }

    /**
     * Returns the number of Version rows.
     *
     * @param array $criteria Optional criteria to filter the count
     * @return int The number of rows
     */
    public function count(array $criteria = []): int
    {
        return (int) $this->countBy($criteria);
    }

    /**
     * Count Version entities based on given criteria.
     *
     * @param array $criteria Criteria to filter the count
     * @return int
     */
    public function countBy(array $criteria): int
    {
        $queryBuilder = $this->createQueryBuilder('v')
                             ->select('count(v)');

        if (!empty($criteria)) {
            foreach ($criteria as $key => $value) {
                $queryBuilder->andWhere("v.$key = :$key")
                             ->setParameter($key, $value);
            }
        }

        return (int) $queryBuilder->getQuery()->getSingleScalarResult();
    }
}

